import React from 'react';
import { AppBar, IconButton, Toolbar, Typography, Menu, MenuItem } from '@material-ui/core'
import MenuIcon from '@material-ui/icons/Menu'
import AccountCircle from '@material-ui/icons/AccountCircle';
import { makeStyles } from '@material-ui/core/styles'
import { ClickAwayListener } from '@material-ui/core';

const useStyle = makeStyles(themee => ({
    root: {
        flexGrow: 1,
        backgroundColor: 'rgba(27, 118, 210, 1)'
    },
    title: {
        flexGrow: 1
    }
}))


const NavBar = ({title}) => {

    const [open, setOpen] =  React.useState(false)
    const classes = useStyle()

    return (
        <ClickAwayListener onClickAway={()=>setOpen(false)}>
        <AppBar position="static" className={classes.root}>
            <Toolbar variant="dense">
                <IconButton edge="start" aria-label="menu">
                    <MenuIcon />
                </IconButton>
                <Typography className={classes.title} variant="h6" color="inherit">
                    {title}
                </Typography>

                    <IconButton onClick={()=>setOpen(!open)}  edge="start" aria-label="logout">
                        <AccountCircle />
                    </IconButton>
                    <Menu
                        id="menu-appbar"
                        open={open}
                        handleClose={open}
                        anchorEl={open}
                        anchorOrigin={{
                            vertical: 'top',
                            horizontal: 'right',
                        }}
                        keepMounted
                        transformOrigin={{
                            vertical: 'top',
                            horizontal: 'right',
                        }}
                    >
                        <MenuItem>Profile</MenuItem>
                        <MenuItem>logout</MenuItem>
                    </Menu>
            </Toolbar>
        </AppBar>
        </ClickAwayListener>
    )
}

export default NavBar
