import auth from 'component/login/controller/auth'

const current_user = (state = [], action) => {
    switch(action.type){
        case 'SIGN_IN':
            return state = [
                {
                    token: auth.curr_user,
                    isLogged: true
                }
            ]
        default:
            return state = false

    }
}

export default current_user
